import React, { useState, useEffect } from "react";
import styled from "styled-components";

import GoogleMapReact from "google-map-react";
// import 'google-map-react/dist/index.css'

import LOS_ANGELES_CENTER from "./const/la_center";

import Marker from "./components/Marker";
import Information from "./components/Information";

const Wrapper = styled.main`
  width: 100%;
  height: 100%;
  display: "flex"
`;

const SubWrapper1 = styled.div`
  width: 70%;
  height: 100%;
`;

const SubWrapper2 = styled.div`
  width: 30%;
  height: 100%;
`;

const App = () => {
  const [places, setPlaces] = useState([]);

  const fetchPlaces = async () => {
    fetch("http://0.0.0.0/crash_events/list/all")
      .then((response) => {
        let data = response.json();
        console.log(data);
        return data;
      })
      .then((data) => setPlaces(data));
  };

  useEffect(() => {
    fetchPlaces();
  }, []);

  if (!places || places.length === 0) {
    return null;
  }

  return (
    <Wrapper>
    <div style={{}}><SubWrapper1>
        <GoogleMapReact
          bootstrapURLKeys={{
            key: "AIzaSyBZhy1F8pC2eoIVpENpXxdZVGGOkhjhJD4",
            language: "en",
          }}
          defaultZoom={14}
          defaultCenter={LOS_ANGELES_CENTER}
        >
          {places.map((place) => (
            <Marker
              key={place.report_number.toString()}
              text={`${place.city} ${place.county}`}
              lat={place.latitude}
              lng={place.longitude}
            />
          ))}
        </GoogleMapReact>
      </SubWrapper1>
      <SubWrapper2>
        <Information></Information>
      </SubWrapper2></div>

    </Wrapper>
  );
};

export default App;
